import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [weather, setWeather] = useState();

  useEffect(() => {
    const fetchWeatherData = async () => {
      try {
        const response = await axios.get(
          // "https://api.openweathermap.org/data/3.0/weather?q=Tokyo,jp&appid=b8f3259f2ef9d894c1026fec139d82d8&units=metric"
          "https://api.openweathermap.org/data/3.0/onecall?lat=35.68944&lon=139.69167&appid=3ad81361fc5a5c9551901dc2d8f95157&units=metric"
        );
        setWeather(response.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchWeatherData();
  }, []);

  return (
    <div className="bg-blue-500 text-white p-5">
      <h1 className="text-3xl font-bold">Today's Weather</h1>
      {weather && (
        <>
          <p className="text-2xl">
            {weather.main}, {weather.sys.country}
          </p>
          <p className="text-xl">{weather.weather[0].main}</p>
          <p className="text-xl">{weather.main.temp}Â°C</p>
        </>
      )}
    </div>
  );
}

export default App;
